import React, { createContext, useContext, useMemo, useState } from 'react';

const CartCtx = createContext();

export function CartProvider({ children }) {
  const [items, setItems] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('cart') || '[]');
    } catch {
      return [];
    }
  });

  const persist = (nextOrFn) => {
    const next = typeof nextOrFn === 'function' ? nextOrFn(items) : nextOrFn;
    setItems(next);
    localStorage.setItem('cart', JSON.stringify(next));
  };

  const add = (product, qty = 1) => {
    persist(() => {
      const existing = items.find((i) => i._id === product._id);
      if (existing) {
        return items.map((i) => (i._id === product._id ? { ...i, quantity: (i.quantity || 1) + qty } : i));
      }
      return [...items, { ...product, quantity: qty }];
    });
  };

  const remove = (id) => persist(items.filter((i) => i._id !== id));
  const clear = () => persist([]);
  const setQuantity = (id, qty) => persist(items.map((i) => (i._id === id ? { ...i, quantity: qty } : i)));

  const totals = useMemo(() => {
    const toNum = (s) => {
      const n = Number(String(s).replace(/[^0-9.]/g, ''));
      return isNaN(n) ? 0 : n;
    };
    const subtotal = items.reduce((sum, it) => sum + toNum(it.amount) * (it.quantity || 1), 0);
    return { subtotal };
  }, [items]);

  const value = { items, add, remove, clear, setQuantity, totals };
  return <CartCtx.Provider value={value}>{children}</CartCtx.Provider>;
}

export const useCart = () => useContext(CartCtx);
