import React, { useState, useEffect } from 'react';
import axios from '../api/axios';
import { useAuth } from '../context/AuthContext';

export default function SellerDashboard() {
  const { user, logout } = useAuth();
  const [products, setProducts] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState(''); // use string now
  const [image, setImage] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  async function fetchProducts() {
    try {
      const res = await axios.get('/products/mine');
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!name || !amount.trim()) {
      setError('Please enter valid product name and amount');
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('amount', amount); // as string
    if (image) formData.append('image', image);

    try {
      await axios.post('/products', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setName('');
      setDescription('');
      setAmount('');
      setImage(null);
      setError('');
      fetchProducts();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add product');
    }
  }

  return (
    <div>
      <h2>Seller Dashboard</h2>
      <p>Welcome, {user.name} <button onClick={logout}>Logout</button></p>
      
      <h3>Add Product</h3>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
        <textarea placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} />
        <input
          type="text"
          placeholder="Amount in Taka (e.g. 5000)"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          required
        />
        <input type="file" onChange={e => setImage(e.target.files[0])} />
        <button type="submit">Add Product</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <h3>My Products</h3>
      <ul>
        {products.map(p => (
          <li key={p._id}>
            <strong>{p.name}</strong> - {p.amount} Taka<br />
            {p.imageUrl && (
              <img
                src={`http://localhost:5000/${p.imageUrl}`}
                alt={p.name}
                width="100"
              />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
